import React from 'react'
import { useState } from 'react';
import { FaTrashAlt } from 'react-icons/fa';
import { FaEdit } from 'react-icons/fa';

const Content = () => {

const [items , setItems] = useState(
  [{
    id: 1,
    title: 'Title 1',
    item: 'Description 1'
  },
  {
    id: 2,
    title: 'Title 2',
    item: 'Description 2'
  },
  {
    id: 3,
    title: 'Title 3',
    item: 'Description 3'
  },
]
);

const handlechange = (id) =>{
  setItems(items.map((item) => {
    if(item.id === id){
      return {
      ...item,
        checked:!item.checked
      }
    }
    return item
  }))
};

const handledelete =(id) => {
  setItems(items.filter((item) => item.id!== id))
};


const handleEdit = (id) => {
  setItems(items.map((item) => {
    if (item.id === id) {
      const updatedItem = {
        ...item,
        title: "New Title" // Replace "New Title" with the desired new title
      };
      return updatedItem;
    }
    return item;
  }));
};




// const handleEdit = (id) => {
//   setItems(items.map((item) => {
//     if(item.id === id){
//       return {
//      ...item,
//         checked:!item.checked
//       }
//     }
//     return item
//   }))
// }

  return (
    <main>
       <ul>
          {
            items.map((item) => (
                          <li key={item.id}>
                           <input type='checkbox'
                           onChange={() => handlechange(item.id)}
                           checked={item.checked}
                           />
                           <label>{item.item}</label>
                           <FaTrashAlt 
                           role='button'
                           onClick={() => handledelete(item.id)}
                           tableIndex='0'
                           />
                           <FaEdit
                           role='button'
                           onClick={() => handleEdit(item.id)} />
                           </li>
          ))}
       </ul>
    </main>

  )
}

export default Content
