    <main>
       <ul>
          {
            items.map((item) => (
                          <li key={item.id}>
                           <input type='checkbox'
                           onChange={() => handlechange(item.id)}
                           checked={item.checked}
                           />
                           <label>{item.item}</label>
                           <FaTrashAlt 
                           role='button'
                           onClick={() => handledelete(item.id)}
                           tableIndex='0'
                           />
                           </li>
          ))}
       </ul>
    </main>

  )
}

export default Content
